package LeetCodeExc;


import java.util.HashMap;
import java.util.Map;
import java.util.Map.Entry;

/***
 * 
 * You are given two strings s and t.

String t is generated by random shuffling string s and then add one more letter at a random position.

Return the letter that was added to t.

 

Example 1:

Input: s = "abcd", t = "abcde"
Output: "e"
Explanation: 'e' is the letter that was added.
Example 2:

Input: s = "", t = "y"
Output: "y"
 

Constraints:

0 <= s.length <= 1000
t.length == s.length + 1
s and t consist of lowercase English letters.
 * @author ringo
 *
 */
public class FindTheDifference {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String s ="";

		String t ="";
		
		
		
		
		System.out.println(findTheDifference(s, t));
	}

	
	
	
public static char findTheDifference(String s, String t) {
        if(s.isEmpty()) return t.charAt(0);
	
	Map<Character,Integer>  mapS = new HashMap<>();
	
	Map<Character,Integer>  mapT = new HashMap<>();
	for(char i : s.toCharArray()){
		
		mapS.put(i, mapS.get(i) != null? mapS.get(i) + 1:1);
		
	}
	
	for(char j : t.toCharArray()){
		
		if(!mapS.containsKey(j)){
			return j;
		}else{
			mapT.put(j, mapT.get(j) != null? mapT.get(j) + 1:1);
		}
		
	}
	
	
	for(Entry<Character, Integer> entry : mapS.entrySet()){
		
		if(entry.getValue()!= mapT.get(entry.getKey())){
			return (char) entry.getKey();
		}
	}
	
	
	
		return '-';
    }
}
